import { RECEIVE_COMPANY } from '../actions/company_show_actions';

export default CompanyReducer = (oldState = {}, action) => {
  Object.freeze(oldState);
  switch(action.type) {
    case RECEIVE_COMPANY:
      return Object.assign({}, oldState, action.company);
    default:
      return oldState;
  }
};
